// src/pages/Notifications.js - NOTIFICATION SYSTEM
import React, { useState, useEffect } from 'react';
import { Bell, Check, Trash2, Trophy, Users, Calendar, DollarSign } from 'lucide-react';
import { getNotifications, markNotificationRead, markAllNotificationsRead } from '../utils/api';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [filter, setFilter] = useState('all');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { user } = useAuth();

  useEffect(() => {
    fetchNotifications();
  }, [filter]);

  const fetchNotifications = async () => {
    try {
      setLoading(true);
      const params = filter === 'unread' ? { unread: true } : {};
      const res = await getNotifications(params);
      setNotifications(res.data.notifications);
    } catch (error) {
      console.error('Error fetching notifications:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleMarkRead = async (id) => {
    try {
      await markNotificationRead(id);
      setNotifications(notifications.map(n =>
        n._id === id ? { ...n, isRead: true } : n
      ));
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const handleMarkAllRead = async () => {
    try {
      await markAllNotificationsRead();
      setNotifications(notifications.map(n => ({ ...n, isRead: true })));
    } catch (error) {
      console.error('Error marking all as read:', error);
    }
  };

  const handleNotificationClick = async (notification) => {
    if (!notification.isRead) {
      await handleMarkRead(notification._id);
    }
    if (notification.actionUrl) {
      navigate(notification.actionUrl);
    }
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'match_invite':
        return <Trophy className="text-yellow-500" size={20} />;
      case 'team_request':
        return <Users className="text-blue-500" size={20} />;
      case 'booking_confirmation':
        return <Calendar className="text-green-500" size={20} />;
      case 'payment':
        return <DollarSign className="text-purple-500" size={20} />;
      case 'new_booking':
        return <Calendar className="text-orange-500" size={20} />;
      default:
        return <Bell className="text-gray-500" size={20} />;
    }
  };

  const unreadCount = notifications.filter(n => !n.isRead).length;

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-green-500"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      <div className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-3">
            <Bell className="text-green-500" size={32} />
            <h1 className="text-3xl font-bold">Notifications</h1>
            {unreadCount > 0 && (
              <span className="px-3 py-1 bg-red-500 text-white rounded-full text-sm font-semibold">
                {unreadCount} new
              </span>
            )}
          </div>

          {unreadCount > 0 && (
            <button
              onClick={handleMarkAllRead}
              className="flex items-center space-x-2 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition"
            >
              <Check size={16} />
              <span>Mark All Read</span>
            </button>
          )}
        </div>

        {/* Filter Tabs */}
        <div className="flex space-x-2 bg-white dark:bg-gray-800 rounded-lg p-1 shadow">
          {['all', 'unread', 'match_invite', 'booking_confirmation', 'new_booking'].map((filterType) => (
            <button
              key={filterType}
              onClick={() => setFilter(filterType)}
              className={`flex-1 px-4 py-2 rounded-lg transition ${
                filter === filterType
                  ? 'bg-gradient-to-r from-green-500 to-blue-500 text-white'
                  : 'hover:bg-gray-100 dark:hover:bg-gray-700'
              }`}
            >
              {filterType.replace('_', ' ').charAt(0).toUpperCase() + filterType.slice(1).replace('_', ' ')}
            </button>
          ))}
        </div>
      </div>

      {/* Notifications List */}
      <div className="space-y-3">
        {notifications.length === 0 ? (
          <div className="text-center py-12 bg-white dark:bg-gray-800 rounded-lg">
            <Bell className="mx-auto text-gray-400 mb-4" size={48} />
            <p className="text-gray-500">No notifications</p>
          </div>
        ) : (
          notifications.map((notification) => (
            <div
              key={notification._id}
              onClick={() => handleNotificationClick(notification)}
              className={`p-4 rounded-lg shadow transition cursor-pointer ${
                notification.isRead
                  ? 'bg-white dark:bg-gray-800'
                  : 'bg-green-50 dark:bg-green-900 border-l-4 border-green-500'
              } hover:shadow-lg`}
            >
              <div className="flex items-start space-x-4">
                <div className="flex-shrink-0 mt-1">
                  {getNotificationIcon(notification.type)}
                </div>

                <div className="flex-1 min-w-0">
                  <div className="flex items-start justify-between mb-1">
                    <p className="font-semibold">{notification.title}</p>
                    <span className="text-xs text-gray-500 whitespace-nowrap ml-2">
                      {new Date(notification.createdAt).toLocaleString()}
                    </span>
                  </div>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    {notification.message}
                  </p>

                  {/* Show additional details for new_booking notifications */}
                  {notification.type === 'new_booking' && notification.data && (
                    <div className="mt-2 p-2 bg-orange-50 dark:bg-orange-900 rounded text-xs">
                      <p><strong>Customer:</strong> {notification.data.customerName}</p>
                      <p><strong>Amount:</strong> ₹{notification.data.totalAmount}</p>
                      <p><strong>Date:</strong> {new Date(notification.data.bookingDate).toLocaleDateString()}</p>
                    </div>
                  )}

                  {notification.actionUrl && (
                    <button className="mt-2 text-sm text-green-500 hover:underline">
                      View Details →
                    </button>
                  )}
                </div>

                {!notification.isRead && (
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleMarkRead(notification._id);
                    }}
                    className="flex-shrink-0 p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded transition"
                    title="Mark as read"
                  >
                    <Check size={16} />
                  </button>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Notifications;
