// ============================================
// src/pages/CreateTurf.js
// ============================================
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { MapPin, Plus, X, Clock } from 'lucide-react';
import { createTurf } from '../utils/api';

const CreateTurf = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    location: {
      address: '',
      city: '',
      state: '',
      pincode: '',
      coordinates: { coordinates: [0, 0] }
    },
    pricing: {
      basePrice: '',
      weekendPrice: '',
      peakHourPrice: ''
    },
    amenities: [],
    specifications: {
      pitchType: 'turf',
      dimensions: '',
      capacity: '',
      lightingAvailable: false
    }
  });
  const [newAmenity, setNewAmenity] = useState('');
  const [slots, setSlots] = useState([{ startTime: '06:00', endTime: '08:00' }]);
  const [loading, setLoading] = useState(false);

  const commonAmenities = [
    'Parking', 'Changing Room', 'Water', 'Lights', 
    'Washroom', 'Canteen', 'First Aid', 'Seating'
  ];

  const handleAddAmenity = (amenity) => {
    if (!formData.amenities.includes(amenity)) {
      setFormData({
        ...formData,
        amenities: [...formData.amenities, amenity]
      });
    }
  };

  const handleRemoveAmenity = (amenity) => {
    setFormData({
      ...formData,
      amenities: formData.amenities.filter(a => a !== amenity)
    });
  };

  const handleAddSlot = () => {
    setSlots([...slots, { startTime: '', endTime: '' }]);
  };

  const handleRemoveSlot = (index) => {
    setSlots(slots.filter((_, i) => i !== index));
  };

  const handleSlotChange = (index, field, value) => {
    const newSlots = [...slots];
    newSlots[index][field] = value;
    setSlots(newSlots);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const turfData = {
        ...formData,
        availability: [{
          date: new Date(),
          slots: slots.map(slot => ({ ...slot, isBooked: false }))
        }]
      };

      await createTurf(turfData);
      alert('Turf created successfully!');
      navigate('/dashboard');
    } catch (error) {
      console.error('Error creating turf:', error);
      alert('Failed to create turf');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      <h1 className="text-3xl font-bold mb-8">List Your Turf</h1>

      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Basic Information */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <h2 className="text-xl font-bold mb-4">Basic Information</h2>
          
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">Turf Name *</label>
              <input
                type="text"
                required
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
                placeholder="e.g., Champions Arena"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Description</label>
              <textarea
                rows={4}
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className="w-full px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
                placeholder="Describe your turf..."
              />
            </div>
          </div>
        </div>

        {/* Location */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <h2 className="text-xl font-bold mb-4 flex items-center">
            <MapPin className="mr-2" size={20} />
            Location
          </h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="md:col-span-2">
              <label className="block text-sm font-medium mb-2">Address *</label>
              <input
                type="text"
                required
                value={formData.location.address}
                onChange={(e) => setFormData({
                  ...formData,
                  location: { ...formData.location, address: e.target.value }
                })}
                className="w-full px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">City *</label>
              <input
                type="text"
                required
                value={formData.location.city}
                onChange={(e) => setFormData({
                  ...formData,
                  location: { ...formData.location, city: e.target.value }
                })}
                className="w-full px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">State *</label>
              <input
                type="text"
                required
                value={formData.location.state}
                onChange={(e) => setFormData({
                  ...formData,
                  location: { ...formData.location, state: e.target.value }
                })}
                className="w-full px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
              />
            </div>
          </div>
        </div>

        {/* Pricing */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <h2 className="text-xl font-bold mb-4">Pricing (per hour)</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2">Base Price (₹) *</label>
              <input
                type="number"
                required
                value={formData.pricing.basePrice}
                onChange={(e) => setFormData({
                  ...formData,
                  pricing: { ...formData.pricing, basePrice: e.target.value }
                })}
                className="w-full px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Weekend Price (₹)</label>
              <input
                type="number"
                value={formData.pricing.weekendPrice}
                onChange={(e) => setFormData({
                  ...formData,
                  pricing: { ...formData.pricing, weekendPrice: e.target.value }
                })}
                className="w-full px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Peak Hour Price (₹)</label>
              <input
                type="number"
                value={formData.pricing.peakHourPrice}
                onChange={(e) => setFormData({
                  ...formData,
                  pricing: { ...formData.pricing, peakHourPrice: e.target.value }
                })}
                className="w-full px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
              />
            </div>
          </div>
        </div>

        {/* Time Slots */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-bold flex items-center">
              <Clock className="mr-2" size={20} />
              Available Time Slots
            </h2>
            <button
              type="button"
              onClick={handleAddSlot}
              className="flex items-center space-x-2 px-3 py-1 bg-green-500 text-white rounded-lg hover:bg-green-600"
            >
              <Plus size={16} />
              <span>Add Slot</span>
            </button>
          </div>

          <div className="space-y-3">
            {slots.map((slot, index) => (
              <div key={index} className="flex items-center space-x-3">
                <input
                  type="time"
                  value={slot.startTime}
                  onChange={(e) => handleSlotChange(index, 'startTime', e.target.value)}
                  className="px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
                  required
                />
                <span>to</span>
                <input
                  type="time"
                  value={slot.endTime}
                  onChange={(e) => handleSlotChange(index, 'endTime', e.target.value)}
                  className="px-4 py-2 border dark:border-gray-600 rounded-lg dark:bg-gray-700"
                  required
                />
                {slots.length > 1 && (
                  <button
                    type="button"
                    onClick={() => handleRemoveSlot(index)}
                    className="p-2 text-red-500 hover:bg-red-50 dark:hover:bg-red-900 rounded"
                  >
                    <X size={20} />
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Amenities */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <h2 className="text-xl font-bold mb-4">Amenities</h2>
          
          <div className="flex flex-wrap gap-2 mb-4">
            {commonAmenities.map((amenity) => (
              <button
                key={amenity}
                type="button"
                onClick={() => handleAddAmenity(amenity)}
                className={`px-3 py-1 rounded-full text-sm transition ${
                  formData.amenities.includes(amenity)
                    ? 'bg-green-500 text-white'
                    : 'bg-gray-200 dark:bg-gray-700 hover:bg-gray-300'
                }`}
              >
                {amenity}
              </button>
            ))}
          </div>

          {formData.amenities.length > 0 && (
            <div className="mt-4 flex flex-wrap gap-2">
              {formData.amenities.map((amenity, index) => (
                <span
                  key={index}
                  className="flex items-center space-x-1 px-3 py-1 bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 rounded-full"
                >
                  <span>{amenity}</span>
                  <button
                    type="button"
                    onClick={() => handleRemoveAmenity(amenity)}
                    className="hover:text-red-500"
                  >
                    <X size={14} />
                  </button>
                </span>
              ))}
            </div>
          )}
        </div>

        {/* Submit */}
        <div className="flex justify-end space-x-4">
          <button
            type="button"
            onClick={() => navigate(-1)}
            className="px-6 py-3 border dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700"
          >
            Cancel
          </button>
          <button
            type="submit"
            disabled={loading}
            className="px-6 py-3 bg-gradient-to-r from-green-500 to-blue-500 text-white rounded-lg hover:opacity-90 transition disabled:opacity-50"
          >
            {loading ? 'Creating...' : 'Create Turf'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateTurf;
